import React, { useReducer } from 'react';

import postContext from './postContext';
import postReducer from './postReducer';
import {
    OBTENER_POSTS,
    AGREGAR_POST,
    POST_EXITOSO,
    POST_ERROR,
    OCULTAR_ALERTA,
    POST_ACTUAL,
    ELIMINAR_POST,
    ACTUALIZAR_POST
} from '../../types';

import clienteAxios from '../../config/axios';

const PostState = props => {

    const initialState = {
        posts : [],
        post: [{}],
        mensaje: {}
    }

    const timeOut = 2000;

    // dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(postReducer, initialState);

    // obtener los posts
    const obtenerPosts = async () => {
        try {
            const url = `https://jsonplaceholder.typicode.com/posts`;
            const resultado = await clienteAxios.get(url);
            console.log("hice la consulta");
            dispatch({
                type: OBTENER_POSTS,
                payload: resultado.data.posts
            });
        } catch (error) {
            const alerta = {
                msg: 'Hubo un error',
                categoria: 'alerta-error'
            }
            dispatch({
                type: POST_ERROR,
                payload: alerta
            })
        }
    }
    
    // agregar nueva post
    const agregarPost = async post => {

        try {
            const resultado = await clienteAxios.post('/api/posts', post);
            
            const alerta = {
                msg: 'Post agregado exitosamente',
                categoria: 'alerta-ok'
            }
            
            dispatch({
                type: POST_EXITOSO,
                payload: alerta
            })

            // insertar el post en el state
            dispatch({
                type: AGREGAR_POST,
                payload: resultado.data
            })
        } catch (error) {
            const alerta = {
                msg: 'Hubo un error',
                categoria: 'alerta-error'
            }
            dispatch({
                type: POST_ERROR,
                payload: alerta
            })
        }

        // Limpia la alerta después de 3 segundos
        setTimeout(() => {
            dispatch({
                type: OCULTAR_ALERTA
            })
        }, timeOut); 
    }

    // selecciona el post que el usuario dio click
    const postActual = postId => {
        dispatch({
            type: POST_ACTUAL,
            payload: postId
        })
    }

    // edita o modifica una post
    const actualizarPost = async post => {
        try {
            const resultado = await clienteAxios.put(`/api/posts/${post._id}`, post);
            
            const alerta = {
                msg: 'Post editado exitosamente',
                categoria: 'alerta-ok'
            }
            
            dispatch({
                type: POST_EXITOSO,
                payload: alerta
            })
            
            dispatch({
                type: ACTUALIZAR_POST,
                payload: resultado.data.post
            })
        } catch (error) {
            const alerta = {
                msg: 'Hubo un error',
                categoria: 'alerta-error'
            }
            dispatch({
                type: POST_ERROR,
                payload: alerta
            })
        }

        // Limpia la alerta después de 3 segundos
        setTimeout(() => {
            dispatch({
                type: OCULTAR_ALERTA
            })
        }, timeOut); 
    }

    // elimina un post
    const eliminarPost = async postId => {
        try {
            
            await clienteAxios.delete(`/api/posts/${postId}`);
            
            const alerta = {
                msg: 'Post eliminado exitosamente',
                categoria: 'alerta-ok'
            }
            
            dispatch({
                type: POST_EXITOSO,
                payload: alerta
            })

            dispatch({
                type: ELIMINAR_POST,
                payload: postId
            })
                        
        } catch (error) {
            const alerta = {
                msg: 'Hubo un error',
                categoria: 'alerta-error'
            }
            dispatch({
                type: POST_ERROR,
                payload: alerta
            })
        }

        // Limpia la alerta después de 3 segundos
        setTimeout(() => {
            dispatch({
                type: OCULTAR_ALERTA
            })
        }, timeOut); 
    }

    return (
        <postContext.Provider
            value={{
                posts: state.posts,
                post: state.post,
                mensaje: state.mensaje,
                obtenerPosts,
                agregarPost,
                postActual,
                actualizarPost,
                eliminarPost
            }}
        >
            {props.children}
        </postContext.Provider>
    )
}

export default PostState;
